export class Input extends ValidatorComponent{
    render(){
        const { errorMessages, validators, requiredError, validatorListener,width, ...rest } = this.props;
        return (

            <Field 
            {...rest}
            width={width}
            error={!this.state.isValid}
            ref={(r) => { this.input = r; }}
            errormsg={this.errorText()}
            />

        );
    }
    errorText() {
